                <Table striped bordered hover responsive className='table-sm'>
                    <thead>
                        <tr>
                            <th>NAME</th>
                            <th>ID</th>
                            <th>EMAIL</th>
                            <th>PHONE NUMBER</th>
                            <th>RENTS</th>
                            <th>AGE</th>
                            <th>RATING</th>
                        </tr>
                    </thead>
                    <tbody>
                        {/**TODO: loop throguh users and display their info */}
                    </tbody>
                </Table>
            </Container>
        </>
    )
}

export default CompanyUserListScreen
